func:
msg.payload = JSON.parse(msg.payload);
msg.payload.customData = null;
msg.payload.siteId = flow.get("siteId");

if( msg.payload.siteId === undefined ){
    msg.payload.siteId = "default";
}

msg.topicorg = msg.topic;
msg.topic = "hermes/intent/" + msg.payload.intent.intentName; 
//node.warn("NLU direct (chat) - " + msg.topic);
msg.payload = JSON.stringify(msg.payload);
if( msg.topic.includes("ProKNX:superScene") || 
    msg.topic.includes("ProKNX:searchInternet") ||
    msg.topic.includes("ProKNX:cancelAction") ||
    msg.topic.includes("ProKNX:confirmAction") ||
    msg.topic.includes("ProKNX:activateScenes") ||
    msg.topic.includes("ProKNX:helpCall") ||
    msg.topic.includes("ProKNX:setVolume") ||
    msg.topic.includes("ProKNX:decreaseVolume") ||
    msg.topic.includes("ProKNX:increaseVolume") ||
    msg.topic.includes("ProKNX:previous") ||
    msg.topic.includes("ProKNX:next") ||
    msg.topic.includes("ProKNX:play") ||
    msg.topic.includes("ProKNX:pause") ||
    msg.topic.includes("ProKNX:shazam") ||
    //msg.topic.includes("ProKNX:deviceStatusCheck") ||
    msg.topic.includes("ProKNX:currentDateTime") ||
    msg.topic.includes("ProKNX:wasHeard") ||
    msg.topic.includes("ProKNX:wakeUp") ||
    msg.topic.includes("ProKNX:troubleshoot") ||
    msg.topic.includes("ProKNX:systemIP") ||
    msg.topic.includes("ProKNX:sayAgain") 
   ){
    return null;
} else if(msg.topic.includes("ProKNX:deviceStatusCheck")){
    return [null,msg];
} else {
    // skip if on-going Super Scene
    var isSuperScene = global.get("isSuperScene");
    if(isSuperScene===undefined || isSuperScene === false){
        return [msg, null];
    }
}

var lang = global.get("msgLang");
msg.payload = JSON.parse(msg.payload);
if( msg.payload.intent.intentName.toLowerCase().includes("status") || 
    msg.payload.intent.intentName.toLowerCase().includes("scene"))
    return;

var keyword = "";
var slotCount = 0;

for(let i=0; i<msg.payload.slots.length; i++){
    if( msg.payload.slots[i].entity === "light_device" || 
        msg.payload.slots[i].entity === "window_device" || 
        msg.payload.slots[i].entity === "temperature_device" || 
        msg.payload.slots[i].entity === "scene_name" || 
        msg.payload.slots[i].entity === "house_room" ){
            keyword += msg.payload.slots[i].rawValue + " ";
            slotCount++;
    }
}


//node.warn("msg.payload.intent.intentName: "+msg.payload.intent.intentName);
if(slotCount < 2){
    if(msg.payload.intent.intentName.toLowerCase().includes("lights")){
        if(lang.toLowerCase().includes("en"))
            keyword += " lights";
        else if(lang.toLowerCase().includes("de"))
            keyword += " licht";
        else if(lang.toLowerCase().includes("fr"))
            keyword += " lumiere";
    } 
    if(msg.payload.intent.intentName.toLowerCase().includes("thermostat")){
        keyword += " temperature";
    }    
    if(msg.payload.intent.intentName.toLowerCase().includes("shutter")){
        if(lang.toLowerCase().includes("en"))
            keyword += " shutter";
        else if(lang.toLowerCase().includes("de"))
            keyword += " rollladen";
        else if(lang.toLowerCase().includes("fr"))
            keyword += " volet";
    }    
}

msg = { payload: {} };
if(lang.toLowerCase().includes("en"))
    msg.payload.input = "What's the status of " + keyword;
else if(lang.toLowerCase().includes("de"))
    msg.payload.input = "wie ist der status des " + keyword;
else if(lang.toLowerCase().includes("fr"))
    msg.payload.input = "quel est le statut du " + keyword;

return msg;

msg.payload = JSON.parse(msg.payload);
if( msg.payload !== undefined && 
    msg.payload.siteId !== undefined &&
    msg.payload.siteId !== "default"){
        flow.set("siteId",msg.payload.siteId);
}
