// Sample code : control of TV set

function findObjectByKey(array, key, value) {
	items = [];
	var cpt = 0;
	for (var i = 0; i < array.length; i++) {
		if (array[i][key] === value) {
			items[cpt] = array[i];
			cpt++;
		}
	}
	if (items[0] !== undefined) {
		return items;
	} else {
		return null;
	}
}

var lang = global.get("msgLang");
var defaultSpeaker = global.get("defaultSpeaker");
var tvFavorites = global.get("tvFavorites");
var siteIdTv = global.get("siteIdTv");
if(defaultSpeaker === undefined){
    defaultSpeaker = "speaker";
}
msg.cmd={};
msg.payload = JSON.parse(msg.payload);
msg.cmd.siteId = msg.payload.siteId.replace(/_\d+$/, "").trim();
var doAllRooms = false;
var intent = msg.payload.intent.intentName;
if (intent === undefined) 
    return null;

if(msg.payload.input.toLowerCase().includes("snips") === true){
    //node.warn("send to snips: includes 'snips'");
    return null;   
}

var rawRoom = findObjectByKey(msg.payload.slots, 'slotName', 'room');
var rawFavorite = findObjectByKey(msg.payload.slots, 'entity', 'FavoriteType');
var rawSpeaker = findObjectByKey(msg.payload.slots, 'entity', 'SpeakerType');

//node.warn(msg.payload);
//node.warn("rawRoom: "+ JSON.stringify(rawRoom) );
var theRoom = msg.payload.siteId;

if (rawRoom !== null) {
	msg.cmd.room = [];
	for (var i = 0; i < rawRoom.length; i++) {
		msg.cmd.room[i] = rawRoom[i].value.value.toLowerCase();
		//node.warn("msg.cmd.room[i]: "+msg.cmd.room[i] + "   msg.payload.siteId: "+msg.payload.siteId);
		if (msg.cmd.room[i] === "here" || msg.cmd.room[i] === "hier" || msg.cmd.room[i] === "ici") {
			theRoom = msg.payload.siteId;
		} else {
    		if (msg.cmd.room[i] === "all" || msg.cmd.room[i] === "everywhere" || msg.cmd.room[i] === "ueberall" || msg.cmd.room[i] === "partout") {
    		    doAllRooms = true;
    		} else {
    		    theRoom = msg.cmd.room[i].replace(/ /g, "_");
    		}
		}
	}
}

if (rawSpeaker !== null) {
    //node.warn("rawSpeaker: "+JSON.stringify(rawSpeaker));
    msg.payload.speaker = rawSpeaker[0].value.value.trim().toLowerCase();
} else {
    msg.payload.speaker = defaultSpeaker.trim().toLowerCase();
}

msg.payload.siteId = theRoom;

if(msg.payload.siteId.toLowerCase().replace(/_\d+$/,"").replace(/_/g,"") !== siteIdTv.toLowerCase().replace(/_\d+$/,"").replace(/ /g,"")){
    return null;
}

if (rawFavorite !== null) {
    //node.warn("rawFavorite: "+JSON.stringify(rawFavorite));
    msg.payload.favorite = rawFavorite[0].value.value.trim().toLowerCase();
    node.warn("msg.payload.favorite: "+msg.payload.favorite);
}


//node.warn("msg.payload.speaker: "+msg.payload.speaker);

if (msg.payload.intent.confidenceScore < 0.6){
    return null;
}

//node.warn("msg.payload.speaker = " + msg.payload.speaker);

if(msg.payload.speaker !== undefined){
    if(msg.payload.speaker === "tv") {

        global.set("defaultSpeaker", "tv");
        //node.warn("intent " + intent);
        switch (intent) {
            case "ProKNX:next": return [msg, null, null, null, null];
            case "ProKNX:previous": return [null, msg, null, null, null];
        }

        if (msg.payload.asrTokens[0].length <= 1){
            return null;
        }else{
            switch(intent){
            case "ProKNX:increaseVolume": return [null, null, msg, null, null];
            case "ProKNX:decreaseVolume": return [null, null, null, msg, null];
            case "ProKNX:setVolume": flow.set("rawLoudness", findObjectByKey(msg.payload.slots, 'slotName', 'loudness')); flow.set("rawMin", findObjectByKey(msg.payload.slots, 'slotName', 'min'));
                                    flow.set("rawMax", findObjectByKey(msg.payload.slots, 'slotName', 'max')); return [null, null, null, null, msg];
            }
        }
    }
}

var lang = global.get("msgLang");
if(lang.toLowerCase().includes("fr")){

    var channelList = global.get("channelList");
    var appList = global.get("appList");

    if(channelList !== undefined){
        msg.payload = [...msg.payload, ...channelList];
    }
    if(appList !== undefined){
        msg.payload = [...msg.payload, ...appList];
    }

    return msg;
}
else {
    //do nothing
}

var lang = global.get("msgLang");
if(lang.toLowerCase().includes("de")){

    var channelList = global.get("channelList");
    var appList = global.get("appList");

    if(channelList !== undefined){
        msg.payload = [...msg.payload, ...channelList];
    }
    if(appList !== undefined){
        msg.payload = [...msg.payload, ...appList];
    }

    return msg;
}
else {
    //do nothing
}

var lang = global.get("msgLang");
if(lang.toLowerCase().includes("en")){

    var channelList = global.get("channelList");
    var appList = global.get("appList");

    if(channelList !== undefined){
        msg.payload = [...msg.payload, ...channelList];
    }
    if(appList !== undefined){
        msg.payload = [...msg.payload, ...appList];
    }

    return msg;
}
else {
