func:
node.warn("Load from file, save to variable");
try {
    var json = JSON.parse(msg.payload);
    flow.set("heatingSettings", json);
    msg.payload = json;
    return msg;
} catch (e) {
    node.error("Failed to parse, error: " + e + " payload" + msg.payload);
}

if (msg.payload == "dirtyTest") { 
    //node.warn("1 sec poll: updates to write to file?");
    try {
        if (flow.get("heatingSettings.dirty") === true) {
            node.warn("dirty flag is set, save");
            flow.set("heatingSettings.dirty", false);
            msg.payload = JSON.stringify(flow.get("heatingSettings"));
            return msg;
        }
    } catch (e) {
        node.warn("Dirty test failed: " + e + ". This typically happens");
    }
}

function initVariables(){
    var HeatingSettings = {
        dirty: false,
        Heating_Mode: "NORMAL",
        LR_Heating_TargetTemp: 18,
        BE_Heating_TargetTemp: 18,
        BA_Heating_TargetTemp: 18,
        LR_Heating_PresetTempNormal: 18,
        BE_Heating_PresetTempNormal: 18,
        BA_Heating_PresetTempNormal: 18
    };
    flow.set("heatingSettings", HeatingSettings);
    return JSON.stringify(HeatingSettings);
}

if (msg.payload === "init") { 
    node.warn("init variables");
    msg.payload = initVariables();
    return msg
}
