function findSwitchByKey(array, key, value) {
    msg.payload.switch_lights = [];
    var cpt = 0;
    for (var i = 0; i < array.length; i++) {
        if (array[i][key] === value) {
            msg.payload.switch_lights[cpt] = array[i];
            cpt ++;
        }
    }
    return null;
}

function findDimmerByKey(array, key, value) {
    msg.payload.dimmer_lights = [];
    var cpt = 0;
